import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {

  const  [count , setCount] = useState(0);

  const addOne = (value) => {
    if(count < 10){
      setCount(count+1);  
    }
    if(count == 10){
      alert("Please decrease");
    }
  }

  const subOne = () =>{
    if(count > 0){
      setCount(count-1);  
    }
    if(count == 0){
      alert("Please Increase");
    }

  }

  return (
    <div className="App">
        <h3> Counter:{count}</h3>
        <button onClick={ () => addOne(1) }>Add 1</button>
        <button onClick={ () => addOne(1) }>Subtract 1</button>
    </div>
  );
}

export default App;
