import './App.css';
import { useState } from 'react';
import Counter from './Counter/Counter';

function App() {
  const  [count , setCount] = useState(0);
  const addOne = (value) => {
      // setCount( count + value);
      setCount((prev) =>{
        if(prev == 10){
          return 0;
        }
        return prev + value;
      });
  };

  return (
    <div className="App">
        <h3> Counter:{count}</h3>        
          <Counter/>
          <Counter/>
          <Counter/>
    </div>
  );
}


export default App;
