import './App.css';
import { useState } from 'react';
import Counter from './Counter/Counter';

function App() {
  const  [count , setCount] = useState(0);

  return (
    <div className="App">
        <h3> Counter:{count}</h3>        
          <button onClick={ () => Counter(1) }>Increase</button>
          <button onClick={ () => Counter(-1) }>Decrease</button>
          <button onClick={ () => Counter(-1) }>Decrease</button>
        <p>{count % 2 == 0 ? "Even" : "Odd" }</p>
    </div>
  );
}


export default App;
