import './App.css';
import { useState } from 'react';

function App() {

  const  [count , setCount] = useState(0);

  const addOne = (value) => {
      // setCount( count + value);
      setCount((prev) =>{
        if(prev == 10){
          return 0;
        }
        return prev + value;
      });
  };

  if(count >10) {
    return <div> Maximum number</div>
  }

  return (
    <div className="App">
        <h3> Counter:{count}</h3>

          <div>
            <button onClick={ () => addOne(1) }>Add 1</button>
            <button onClick={ () => addOne(-1) }>Subtract 1</button>
          </div>
        )

        <p>{count % 2 == 0 ? "Even" : "Odd" }</p>
    </div>
  );
}

export default App;
