import './App.css';
import { useState } from 'react';

function App() {

  const  [count , setCount] = useState(0);

  const addOne = (value) => {
      // setCount( count + value);
      setCount((prev) =>{
        if(prev == 10){
          return 0;
        }
        return prev + value;
      });
  };

  return (
    <div className="App">
        <h3> Counter:{count}</h3>        
          <button onClick={ () => addOne(1) }>Increase</button>
          <button onClick={ () => addOne(-1) }>Decrease</button>
          <button onClick={ () => addOne(-1) }>Decrease</button>
        <p>{count % 2 == 0 ? "Even" : "Odd" }</p>
    </div>
  );
}


export default App;
