import logo from './logo.svg';
import './App.css';

function App() {

  const  count = 0;

  const addOne = () =>{
    count++;
  }

  return (
    <div className="App">
        <h3> Counter:{count}</h3>
        <button onClick={addOne}>Add 1</button>
    </div>
  );
}

export default App;
